'#room-num').change(function(){				 var room = $('#room-num').val();				 var i;				 var body = "";				 				 for(i = 1; i<=(3*room); i++){					 body += '<option value="' + i + '">'+i+'</option>';				 }				 				 $('#person-num').html(body);				 			 }) ;			  		  });		  		  $(function () {		// Punyaku disabling dates	var nowTemp = new Date();	var now = new Date(nowTemp.getFullYear(), nowTemp.getMonth(), nowTemp.getDate(), 0, 0, 0, 0);	var checkin = $('div#check-in').datepicker({		onRender: function (date) {			return date.valueOf() < now.valueOf() ? 'disabled' : '';		}	}).on('changeDate', function (ev) {		if (ev.date.valueOf() > checkout.date.valueOf()) {			var newDate = new Date(ev.date)			newDate.setDate(newDate.getDate() + 1);			checkout.setValue(newDate);		}				data_waktu.ck_in = ev.date;		checkin.hide();	}).data('datepicker');	var checkout = $('div#check-out').datepicker({		onRender: function (date) {			return date.valueOf() <= checkin.date.valueOf() ? 'disabled' : '';		}	}).on('changeDate', function (ev) {		data_waktu.ck_out = ev.date;		checkout.hide();		var a = new Date(data_waktu.ck_out),			b = new Date(data_waktu.ck_in),			c = 24 * 60 * 60 * 1000,			diffDays = Math.round(Math.abs((a - b) / (c)));				$kamar_pilihan.total_hari = diffDays;		updateJumlahHari(data_waktu.ck_in, data_waktu.ck_out, $kamar_pilihan.total_hari);	}).data('datepicker');});	  	  </script>  </body></html>