<?php require_once('Connections/antares_conn.php'); ?><?phpif (!function_exists("GetSQLValueString")) {function GetSQLValueString($theValue, $theType, $theDefinedValue = "", $theNotDefinedValue = "") {  if (PHP_VERSION < 6) {    $theValue = get_magic_quotes_gpc() ? stripslashes($theValue) : $theValue;  }  $theValue = function_exists("mysql_real_escape_string") ? mysql_real_escape_string($theValue) : mysql_escape_string($theValue);  switch ($theType) {    case "text":      $theValue = ($theValue != "") ? "'" . $theValue . "'" : "NULL";      break;        case "long":    case "int":      $theValue = ($theValue != "") ? intval($theValue) : "NULL";      break;    case "double":      $theValue = ($theValue != "") ? doubleval($theValue) : "NULL";      break;    case "date":      $theValue = ($theValue != "") ? "'" . $theValue . "'" : "NULL";      break;    case "defined":      $theValue = ($theValue != "") ? $theDefinedValue : $theNotDefinedValue;      break;  }  return $theValue;}}mysql_select_db($database_antares_conn, $antares_conn);$query_antares_db = "SELECT * FROM kamar";$antares_db = mysql_query($query_antares_db, $antares_conn) or die(mysql_error());$row_antares_db = mysql_fetch_assoc($antares_db);$totalRows_antares_db = mysql_num_rows($antares_db);?><!doctype html><html><head><meta charset="utf-8"><title>Kamar</title></head><body><table border="1" cellpadding="0" cellspacing="0">	<tr>		<td>no_kamar</td>		<td>tipe</td>		<td>kapasitas</td>		<td>harga</td>		<td>deskripsi</td>		<td>status</td>	</tr>	<?php do { ?>		<tr>			<td><?php echo $row_antares_db['no_kamar']; ?></td>			<td><?php echo $row_antares_db['tipe']; ?></td>			<td><?php echo $row_antares_db['kapasitas']; ?></td>			<td><?php echo $row_antares_db['harga']; ?></td>			<td><?php echo $row_antares_db['deskripsi']; ?></td>			<td><?php echo $row_antares_db['status']; ?></td>		</tr>		<?php } while ($row_antares_db = mysql_fetch_assoc($antares_db)); 