<?php require_once('Connections/antares_conn.php'); ?><?php	$ck_in = "";	$ck_out = "";if (!function_exists("GetSQLValueString")) {function GetSQLValueString($theValue, $theType, $theDefinedValue = "", $theNotDefinedValue = "") {  if (PHP_VERSION < 6) {    $theValue = get_magic_quotes_gpc() ? stripslashes($theValue) : $theValue;  }  $theValue = function_exists("mysql_real_escape_string") ? mysql_real_escape_string($theValue) : mysql_escape_string($theValue);  switch ($theType) {    case "text":      $theValue = ($theValue != "") ? "'" . $theValue . "'" : "NULL";      break;        case "long":    case "int":      $theValue = ($theValue != "") ? intval($theValue) : "NULL";      break;    case "double":      $theValue = ($theValue != "") ? doubleval($theValue) : "NULL";      break;    case "date":      $theValue = ($theValue != "") ? "'" . $theValue . "'" : "NULL";      break;    case "defined":      $theValue = ($theValue != "") ? $theDefinedValue : $theNotDefinedValue;      break;  }  return $theValue;}}mysql_select_db($database_antares_conn, $antares_conn);$query_kamar_rs = "SELECT * FROM kamar WHERE status <> 'Booked' GROUP BY tipe ORDER BY harga ASC";$kamar_rs = mysql_query($query_kamar_rs, $antares_conn) or die(mysql_error());$row_kamar_rs = mysql_fetch_assoc($kamar_rs);$totalRows_kamar_rs = mysql_num_rows($kamar_rs);?><!DOCTYPE html><html lang="en"><head><meta charset="utf-8"><meta http-equiv="X-UA-Compatible" content="IE=edge"><meta name="viewport" content="width=device-width, maximum-scale=1.0, minimum-scale=1.0, initial-scale=1.0" /><meta name="description" content=""><meta name="author" content=""><!-- Utk icon browser --><link rel="shortcut icon" href="favicon.ico"><link rel="icon" href="favicon.png"><!-- Utk iPad --><link rel="apple-touch-icon" href="img/icon/icon-144.png" sizes="144x144"><link rel="apple-touch-icon" href="img/icon/icon-72.png" sizes="72x72"><!-- Utk iPhone --><link rel="apple-tou